mapper.select:
select a.TITLE, a.BIANMA, a.SPTYPE_ID, a.SPBRAND_ID, a.SPUNIT_ID, a.USERNAME, a.GOODS_ID, a.GCOUNT, a.GPRICE, b.NAME BNAME, c.NAME TNAME, d.NAME UNAME from a left join cp_spbrand b on a.SPBRAND_ID = b.SPBRAND_ID left join cp_sptype c on a.SPTYPE_ID = c.SPTYPE_ID left join cp_spunit d on a.SPUNIT_ID = d.SPUNIT_ID where 1=1
and ( a.TITLE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.SHORTDESC LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and a.SPBRAND_ID = #{pd.SPBRAND_ID}
and a.SPTYPE_ID = #{pd.SPTYPE_ID}
and a.GCOUNT != 0 ORDER BY a.BIANMA ASC

mapper.update:
update

set
	GCOUNT = GCOUNT + #{INCOUNT}
where
	GOODS_ID = #{GOODS_ID}
